import DetailsButton from "./DetailsButton";

const UserCard = ({ name, username, website, id }) => {
  const cardUrl = `/users/${id}`;

  return (
    <div className="user-card-container">
      {/* Comment ajouter en props une image personnalisée qui ne se trouve pas dans l'API? */}
      <h1>{name}</h1>
      <h3>@{username}</h3>
      {/* Comment parvenir à ouvrir le lien? */}
      <a href={website}>http://{website}</a>
      {/* Comment appeler le component DetailsButton? */}
      {/* <DetailsButton /> */}
      {/* <br></br> */}

      <button className="user-card-btn">
        <DetailsButton />
      </button>
    </div>
  );
};

export default UserCard;
