  //         <li>suite: {userDetails.address.suite}</li>
  //         <li>city: {userDetails.address.city}</li>
  //         <li>zipcode: {userDetails.address.zipcode}</li>
  //       </div>
  //       <Link to="/">
  //         <button className="user-details-btn">BACK</button>
  //       </Link>
  //     </div>
  //   )
  // );
};

export default UserDetails;
