import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

import "../style/screens/UserDetails.css";

const UserDetails = ({ id }) => {
  const [userDetails, setUserDetails] = useState({});
  let params = useParams();
  console.log("params", params.id);

  useEffect(() => {
    axios
      .get(`https://jsonplaceholder.typicode.com/users/${params.id}`)
      .then((res) => setUserDetails(res.data));
  }, []);

  return (
    <div className="user-details-container">
      <div className="user-details-info">
        <p>- name: {userDetails.name}</p>
        <p>- username: {userDetails.username}</p>
        <p>- email: {userDetails.email}</p>
        <p>- phone: {userDetails.phone}</p>
        <p>- company: {userDetails.company.name}</p>
        <p>- website: {userDetails.website}</p>
        {/* Ajouter l'adresse détaillée avec des li ? */}
        <p>- address:</p>
        <p>- street: {userDetails.address.street}</p>
        <p>- suite: {userDetails.address.suite}</p>
        <p>- city: {userDetails.address.city}</p>
        <p>- zipcode: {userDetails.address.zipcode}</p>
      </div>
      <Link to="/">
        <button className="user-details-btn">BACK</button>
      </Link>
    </div>
  );
};

export default UserDetails;
